-- ----------------------------
-- Table structure for sn_admin
-- ----------------------------
DROP TABLE IF EXISTS `sn_admin`;
CREATE TABLE `sn_admin` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `adm_name` varchar(255) NOT NULL,
  `adm_password` varchar(255) NOT NULL,
  `is_effect` tinyint(1) NOT NULL,
  `is_delete` tinyint(1) NOT NULL,
  `role_id` int(11) NOT NULL,
  `login_time` int(11) NOT NULL,
  `login_ip` varchar(255) NOT NULL,
  `is_department` tinyint(1) NOT NULL COMMENT '用户类型   0：管理员，1：部门',
  `pid` int(11) NOT NULL COMMENT '所属部门编号',
  `work_id` varchar(255) DEFAULT NULL COMMENT '员工编号',
  `referrals_rate` varchar(50) NOT NULL COMMENT '提成系数',
  `referrals_count` int(11) NOT NULL COMMENT '部门成员人数',
  `referrals_money` decimal(11,0) NOT NULL COMMENT '提成金额',
  `real_name` varchar(50) NOT NULL,
  `mobile` varchar(20) NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `unique_adm_name` (`adm_name`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=20 DEFAULT CHARSET=utf8;

