INSERT INTO `sn_article_cate` VALUES ('10', '贷款审核与保障', '<img src=\"./public/attachment/201303/02/17/5131c2460b952.jpg\"/>\r\np2p信贷拥有一套科学有效的信用审核标准和方法，对借款用户进行信用风险分析及信用等级分级。同时p2p信贷建立了完整严谨的风险管理体系包括贷前审核、贷中审查和贷后管理以控制借款逾期违约的风险。从创立开始，p2p信贷借款逾期率一直保持在0.9%以内，为业内最优水平。', '12', '1', '0', '3', '10');
