DROP TABLE IF EXISTS `sn_loan`;
CREATE TABLE `sn_loan` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `type` varchar(5) CHARACTER SET utf8 COLLATE utf8_icelandic_ci NOT NULL,
  `mortgage` tinyint(4) NOT NULL COMMENT '抵押 1有0吴',
  `loanmoney` decimal(10,0) NOT NULL,
  `cycle` char(1) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL COMMENT '还款类型',
  `term` tinyint(4) NOT NULL COMMENT '还款期限',
  `name` varchar(20) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `mobile` varchar(13) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `start` tinyint(4) DEFAULT NULL COMMENT '0 未审核 1同意 2拒绝',
  `user_id` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=gbk;

